const value=ref('') //对话框写的文本内容
const handleMsgSubmit = (msg:string)=>{
	message.success('Send: '+msg);
	inPush.value = true;
	apiAgent.onRequest({content:msg,mode:hasDeep.value? 'r1':'v3'});
	emit('onSend',{
		msg:msg,
		isDeep:hasDeep.value,
		isNet:hasNet.value,
		uploadType:uploadType.value,
		uploadList:fileItems.value,
	});
	value.value = ''
}
// ……
<Sender
  :loading="inPush||recording" 
  :allow-speech="speechOption"
  placeholder="电脑端请用 `Shift + Enter` 键换行" 
  :value="value"
  @submit="handleMsgSubmit"
  @cancel="handleCancel"
  :styles="{input:{maxHeight:'100px'}}"></Sender>
